import React, { Component } from 'react'
import {ProductConsumer} from '../context'
import {Link} from 'react-router-dom'
import {ButtonContainer} from './Button'
import axios from 'axios'
import { values } from 'mobx'


export default class  extends Component {

    state  = 
        {   offer: [],
            car: []}

componentDidMount = () => {
    //const id =  this.props.location.state

    const  id  = this.props.location.state.id

    console.log(id)

    const link2 = 'https://autorentserver.azurewebsites.net/api/offers/target/' + id;

    axios.get(link2,{
        headers : {
            'language':'pl'  
        }
    })
    .then(  (response) => {
        console.log(response.data)
        this.setState({
            offer: response.data
        })
        const link = 'https://autorentserver.azurewebsites.net/api/cars/target/' + this.state.offer.car_id;
            console.log(link)
            axios.get(link,{
                headers : {
                    'language':'pl'  
                }
            })
            .then(  (response) => {
                console.log(response.data)
                this.setState({
                    car: response.data
                })
            })

    })


}
render() {
    return (
                    <div className="container py-5">

                            <div className="row">
                                <div className="col-10 mx-auto text-center text slanted text-blue my-5">
                                    <h1>{this.state.offer.title}</h1>
                                </div>
                            </div>

                            <div className="row">
                                {/* <div className="col-10 mx-auto col-md-6 my-3">
                                    <img src={img} className="img-fluid" alt="product"/>
                                </div> */}

                                <div className="col-10 mx-auto col-md-6 my-3 text-capitalize">
                                    <h1>{this.state.car.brand} {this.state.car.model}</h1>

                                    <h4 className="text-title text uppercas text mudted mt-3 mb-2">
                                        <span className="text-uppercase">Nazwa: {this.state.car.name}</span>
                                    </h4>
                                    <h4 className="text-title text uppercas text mudted mt-3 mb-2">
                                        <span className="text-uppercase">Paliwo: {this.state.car.fuel}</span>
                                    </h4>
                                    <h4 className="text-title text uppercas text mudted mt-3 mb-2">
                                        <span className="text-uppercase">Kolor: {this.state.car.color}</span>
                                    </h4>
                                    <h4 className="text-title text uppercas text mudted mt-3 mb-2">
                                        <span className="text-uppercase">Kraj pochodzenia: {this.state.car.origin_country}</span>
                                    </h4>
                                    <h4 className="text-title text uppercas text mudted mt-3 mb-2">
                                        <span className="text-uppercase">Skrzyna biegow: {this.state.car.transmission}</span>
                                    </h4>
                                    <h4 className="text-title text uppercas text mudted mt-3 mb-2">
                                        <span className="text-uppercase">Lokalizacja: {this.state.offer.location}</span>
                                    </h4>
                                    <h4 className="text-title text uppercas text mudted mt-3 mb-2">
                                        <span className="text-uppercase">Stan techniczny: {this.state.car.technical_remarks}</span>
                                    </h4>
                                    <h4 className="text-title text uppercas text mudted mt-3 mb-2">
                                        <span className="text-uppercase">Numer telefonu: {this.state.offer.phone}</span>
                                    </h4>
                                    <h4 className="text-title text uppercas text mudted mt-3 mb-2">
                                        <span className="text-uppercase">Email: {this.state.offer.email}</span>
                                    </h4>
                                    <h3 className="text-blue">
                                        <strong>
                                            Cena: {this.state.car.price}zł/dzień
                                        </strong>
                                    </h3>
                                    <h3 className="text-capitalize font-weight-bold mt-3 mb-0">
                                        Informacje od właściciela:
                                    </h3>
                                    <p className="text-muted lead">{this.state.offer.description}</p>
                                    <div>
                                        <Link to="/">
                                            <ButtonContainer>
                                                Powrót
                                            </ButtonContainer>
                                        </Link>

                                        <ProductConsumer>
                                            {(value) => (
                                               <ButtonContainer
                                            fav
                                            disabled={this.state.car.inFavourites?true:false}
                                            onClick={()=>{


                                                    value.addToFavourite(this.state.car,this.state.offer.id);
                                                    value.openModal(this.state.car);


                                            }}>
                                                {this.state.car.inFavourites?"Polubiono":"Dodaj do polubionych"}
                                            </ButtonContainer> 
                                            )}

                                        </ProductConsumer>
                                    </div>
                                </div>
                            </div>
                    </div>
    )
}
}
