import React, { Component } from 'react'
import Title from '../Title';
import axios from 'axios';


export default class AddCar extends Component {

    handleSubmit = (event) => {

        event.preventDefault();

        axios.post('https://autorentserver.azurewebsites.net/api/cars/create', {
            name: event.target.name.value,
            brand: event.target.brand.value,
            model: event.target.model.value,
            power: event.target.power.value,
            engine: event.target.engine.value,
            color: event.target.color.value,
            fuel: event.target.fuel.value,
            transmission: event.target.transmission.value,
            technical_remarks: event.target.technical_state.value,
            origin_country: event.target.country_origin.value,
            price: event.target.price.value
    },{
            headers: {
                'auth-token':this.props.userdata.authToken
            }
        })
        .then(function (response) {
            console.log(response);
            alert(response);
        })
        .catch(function (error) {
            console.log(error);
            console.log(error.response.data);
        });
    }

    getInputStyle = () => {
        return {
            width: "50%",
            padding: "12px 20px",
            margin: "8px 0",
            display: "inline-block",
            border: "1px solid #ccc",
            borderRadius: "4px",
            boxSizing: "border-box"
        }
    }
    getLabelStyle = () => {
        return {
            width: "30%",
            textAlign: "right",
            padding: "12px 20px",
            margin: "8px 0",
            display: "inline-block",
            boxSizing: "border-box"
        }
    }
    getButtonStyle = () => {
        return {
            width: "60%",
            color: "white",
            backgroundColor: "blue",
            display: "inline-block",
            boxSizing: "border-box",
            marginLeft: "20%",
            padding: "10px"
        }
    }
    render() {
        return (
            <React.Fragment>
                <Title title="Dodaj własny samochód" />
                <div style={{borderRadius: "5px", backgroundColor: "#f2f2f2", padding: "20px", margin: "auto"}}>
                <form onSubmit={this.handleSubmit}>

                <label for="fname" style={this.getLabelStyle()}>Nazwa:</label>
                <input type="text" style={this.getInputStyle()} id="fname" name="name" placeholder="Podaj nazwe swojego samochodu"/>

                <label for="fname" style={this.getLabelStyle()}>Marka:</label>
                <input type="text" style={this.getInputStyle()} id="fname" name="brand" placeholder="Podaj markę swojego samochodu"/>

                <label for="fname" style={this.getLabelStyle()}>Model:</label>
                <input type="text" style={this.getInputStyle()} id="fname" name="model" placeholder="Podaj model swojego samochodu"/>

                <label for="fname" style={this.getLabelStyle()}>Moc:</label>
                <input type="text" style={this.getInputStyle()} id="fname" name="power" placeholder="Podaj moc silnika swojego samochodu"/>

                <label for="fname" style={this.getLabelStyle()}>Silnik:</label>
                <input type="text" style={this.getInputStyle()} id="fname" name="engine" placeholder="Podaj pojemnosc silnika swojego samochodu"/>

                <label for="fname" style={this.getLabelStyle()}>Kolor</label>
                <input type="text" style={this.getInputStyle()} id="fname" name="color" placeholder="Podaj kolor swojego samochodu"/>

                <label for="fname" style={this.getLabelStyle()}>Paliwo</label>
                <input type="text" style={this.getInputStyle()} id="fname" name="fuel" placeholder="Podaj paliwo swojego samochodu"/>

                <label for="fname" style={this.getLabelStyle()}>Skrzynia biegów</label>
                <input type="text" style={this.getInputStyle()} id="fname" name="transmission" placeholder="Podaj rodzaj skrzyni biegów swojego samochodu"/>

                <label for="fname" style={this.getLabelStyle()}>Stan techniczny:</label>
                <input type="text" style={this.getInputStyle()} id="fname" name="technical_state" placeholder="Podaj stan swojego samochodu"/>

                <label for="fname" style={this.getLabelStyle()}>Kraj pochodzenia:</label>
                <input type="text" style={this.getInputStyle()} id="fname" name="country_origin" placeholder="Podaj moc silnika swojego samochodu"/>

                <label for="fname" style={this.getLabelStyle()}>Cena:</label>
                <input type="text" style={this.getInputStyle()} id="fname" name="price" placeholder="Podaj cene za wynajem swojego samochodu na jeden dzień"/>

                <input 
                type="submit"
                value="Dodaj samochód"
                className="btn"
                style={this.getButtonStyle()}
                />
                </form>
                </div>



            </React.Fragment>

        )
    }
}
