import React, { Component } from 'react';
import styled from 'styled-components';
import {Link} from 'react-router-dom';
import {ProductConsumer} from '../../context';
import PropTypes from 'prop-types'
import axios from 'axios';

export default class LiveOffer extends Component {
    render() {
        const {id, title, img, price, inFavourites} = this.props.product;
        return (
            <ProductWrapper className="col -9 mx-auto col-md-6 col-lg-3 my-3">
                <div className="card"> 
                <ProductConsumer>
                    {(value) => (                    
                    <div className="img-container p-5" onClick={ () => 
                        value.handleDetail(id)
                    }>
                        <button className="fav-btn"  onClick={()=>{
                            const link = 'https://autorentserver.azurewebsites.net/api/offers/archive/' + this.props.product.id;
                            axios.delete(link, {headers: {
                                'auth-token':this.props.userdata.authToken
                            }})
                        }}>
                        </button>
                    </div>)}
                </ProductConsumer>
                    <div className="card-footer d-flex justify-content-between">
                        <p className="align-self-center mb-0">
                           {title} 
                        </p>
                        <h5 className="text-blue font-italic mb-0">
                            {price}zł/dzień
                        </h5>
                    </div>
                </div>
            </ProductWrapper>
        );
    }
}

LiveOffer.propTypes = {
    product:PropTypes.shape({
        id:PropTypes.number,
        img:PropTypes.string,
        title:PropTypes.string,
        price:PropTypes.string,
        inFavourites:PropTypes.bool
    }).isRequired
}


const ProductWrapper = styled.div`
.card{
    border-color:transparent;
    transition:all 0.5s linear;
}
.card-footer{
    background:transparent;
    border-top transparent;
    transition:all 0.5s linear;
}
&:hover{
    .card{
        border:0.04rem solid rgba(0,0,0,0.2);
        box-shadow:2px 2px 5px 0px rgba(0,0,0,0.2);
    }
    .card-footer{
        background:rgba(247,247,247);
    }
}
.img-container{
    position:relative;
    overflow:hidden
}
.card-img-top{
    transition:all 0.5s linear;
}
.img-container:hover .card-img-top{
    transform: scale(1.2);
}
.fav-btn{
    position: absolute;
    bottom: 0;
    right: 0;
    padding: 0.2rem 0.4rem;
    background: var(--lightBlue);
    border:none;
    color:var(--mainWhite);
    font-size:1.4rem;
    border-radius:0.5rem 0 0 0;
    transform: translate(100%, 100%);
    transition:all 0.5s linear;
}
.img-container:hover .fav-btn{
    transform:translate(0,0);
}
.fav-btn:hover{
    color: var(--mainBlue);
    cursor: pointer;
}
`;
