import axios from 'axios';
import React, { Component } from 'react';
import styled from 'styled-components';
import MessageBox from './MessageBox';

const Container = styled.div`
    display: flex;
    width: 100%;
    height: 50px;
    background-color: #FFEFCE;
    border-top: 2px solid #505050;
`

const MessageInput = styled.input`
    background-color: rgba(255,255,255,0.5);
    border: none;
    outline: none;
    padding-right: 10px;
    padding-left: 10px;
    display: flex;
    flex-grow: 1;
    height: 100%;
`

const SendButton = styled.button`
    width: 80px;
    height: 100%;
    background-color: #D29925;
    border: 0;
    color: white;
    border-left: 1px solid #505050;

    &:hover {
        background-color: #B37E14;
    }
`

class MessagePrompt extends Component {
    state = {
        messageInputValue: ""
    }

    handleChangeMessageInput = (e) => {
        this.setState({messageInputValue:e.target.value})
    }

    handleClickSendButton = () => {
        if (!this.props.current_conversant) {
            return;
        }

        let config = {
            headers: {
                'Content-Type': 'application/json',
                'auth-token': this.props.userdata.authToken
            }
        };

        let data = {
            target_id: this.props.current_conversant.id,
            content: this.state.messageInputValue
        }

        axios.post('https://autorentserver.azurewebsites.net/api/messages/create', data, config)
            .then((res) => {
                this.props.reloadMessages();
                this.setState({messageInputValue:""})
            })
            .catch(err => {
                console.log(err)
            });
    }

    render() { 
        return (
            <Container>
                <MessageInput
                    value={this.state.messageInputValue}
                    onChange={this.handleChangeMessageInput}
                ></MessageInput>
                <SendButton
                    onClick={this.handleClickSendButton}
                >Wy≈õlij</SendButton>
            </Container>
        );
    }
}

export default MessagePrompt;
