import React, { Component } from 'react'
import styled from 'styled-components'
import {ProductConsumer} from '../context'
import {ButtonContainer} from './Button'
import {Link} from 'react-router-dom'

export default class Modal extends Component {
    render() {
        return (
                <ProductConsumer>
                    {(value) =>{
                        const {modalOpen, closeModal} = value;
                        const {img, title, price} = value.modalProdact;
                        if(!modalOpen){
                            return null;
                        }
                        else{
                        return(
                        <ModalContainer>
                                <div className="container">
                                    <div className="row">
                                        <div id="modal" className=
                                        "col-8 mx-auto col-md-6 col-lg-4 text-center text-capitalize p-5">
                                            <h5>dodano do ulubionych</h5>
                                            <img src={img} className="img-fluid" alt="produkt" />
                                            <h5>{title}</h5>
                                            <h5 className="text-muted">Cena: {price}zł/dzień</h5>
                                            <Link to='/'>
                                                <ButtonContainer onClick={ ()=>closeModal()}>
                                                    Oferty
                                                </ButtonContainer>
                                            </Link>
                                            <Link to='/favourites'>
                                                <ButtonContainer fav onClick={()=>closeModal()}>
                                                    Polubione
                                                </ButtonContainer>
                                            </Link>
                                        </div>
                                    </div>
                                </div>
                            </ModalContainer>
                            );
                        }
                    }}
                </ProductConsumer>
        )
    }
}

const ModalContainer = styled.div`
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: rgba(0,0,0,0.3);
    display: flex;
    align-items: center;
    justify-content: center;
    #modal{
        background:var(--mainWhite);
    }
`;
