import React, { Component } from 'react';
import styled from 'styled-components';

const Container = styled.div`
    display: flex;
    width: calc(100%);
    height: 40px;
    min-height: 40px;
    border-bottom: 1px solid #505050;
    align-items: center;
    padding-left: 10px;
    padding-right: 10px;
`

class Message extends Component {
    state = {  }
    render() { 
        let style = {};

        if (this.props.message.sender_id == this.props.user) {
            style.justifyContent = "flex-end";
        }

        return (
            <Container style={style}>{this.props.message.content}</Container>
        );
    }
}

export default Message;
