import React, { Component } from 'react';
import Conversation from './Conversation';
import styled from 'styled-components';

const Container = styled.div`
    display: flex;
    flex-direction: column;
    width: 20%;
    height: 100%;
    border-right: 2px solid #505050;
    background-color: #F9C45A;
`

const Label = styled.div`
    display: flex;
    width: 100%;
    height: 50px;
    font-weight: bold;
    background-color: #B0801E;
    justify-content: center;
    align-items: center;
    color: white;
    border-bottom: 2px solid #505050;
`

class Conversations extends Component {
    renderConversations = () => {

        if (!this.props.conversations && !this.props.user) {
            return;
        }

        let conversants = {}

        this.props.conversations.forEach(c => {
            if (c.sender_id == this.props.user) {
                conversants[c.reciever] = c.reciever_id
            } else {
                conversants[c.sender] = c.sender_id
            }
        });

        return Object.entries(conversants).map(c => {
            return (
                <Conversation handleClickConversation={() => this.props.handleClickConversation({name:c[0],id:c[1]})} username={c[0]} key={c[1]} />
            )
        });
    }

    render() { 
        return (
            <Container>
                <Label>Konwersacje</Label>
                {this.renderConversations()}
            </Container>
        );
    }
}

export default Conversations;
