import React, { Component } from 'react';
import MessagePrompt from './MessagePrompt';
import MessageDisplay from './MessageDisplay';
import styled from 'styled-components';

const Container = styled.div`
    display: flex;
    flex-direction: column;
    flex-grow: 1;
    height: 100%;
    background-color: #FFEFCE;
`

const ConversantLabel = styled.div`
    display: flex;  
    width: 100%;
    height: 50px;
    padding-right: 15px;
    align-items: center;
    justify-content: flex-end;
    font-weight: bold;
    color: white;
    background-color: #D29925;
    border-bottom: 2px solid #505050;
`

class MessageBox extends Component {
    state = {  }
    render() { 
        return (
            <Container>
                <ConversantLabel>
                    {(this.props.current_conversant) ? `Konwersacja z ${this.props.current_conversant.name}` : "Brak wybranej konweracji"}
                </ConversantLabel>
                <MessageDisplay
                    messages={this.props.messages}
                    user={this.props.user}
                />
                <MessagePrompt
                    token={this.props.token}
                    current_conversant={this.props.current_conversant}
                    reloadMessages={this.props.reloadMessages}
                    userdata={this.props.userdata}
                ></MessagePrompt>
            </Container>
        );
    }
}

export default MessageBox;
